import axios from "axios";
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { IAnimal } from "../models/IAnimal";

export const Animals = () =>{

    const [animals, setAnimals] = useState<IAnimal[]>([]);
    const [theAnimals, setTheAnimals] = useState(0);
    const [animalsFed, setAnimalsFed] = useState(false);
    const[isFedDisable, setIsFedDisable] = useState(false);


   useEffect(() => {

    if(animals.length > 0 )return;

    axios.get<IAnimal[]>('https://animals.azurewebsites.net/api/animals')
    .then((response =>{
        // let animalsFromAPI = response.data.map((animal: IAnimal)=>{
        //     return new IAnimal(animal.id, animal.name, animal.latinName, animal.yearOfBirth, animal.shortDescription, animal.longDescription,animal.imageUrl, animal.medicine,animal.isFed, animal.lastFed);
        // });
        setAnimals(response.data);

   }));
});;

function handelFood(){
    setAnimalsFed (true);
    setIsFedDisable (true);
}


let AnimalsApi = animals.map((animal: IAnimal) => {
    let linkAnimal = `/TheAnimals/${animal.id}`;

   return (
       <div key={animal.id}>
           <h1>{animal.name}</h1>
           <img src={animal.imageUrl} width="200px"/>
           <p>Kort Beskrivning om djuret:</p>
           <p>{animal.shortDescription}</p>
           <Link to={linkAnimal}>{animal.name}</Link>
       </div>  

   )
   });
   localStorage.setItem("Animals", JSON.stringify(animals));
    return(
    <>
    <div>{AnimalsApi}</div>
    <div>Animal {animals.length}</div>
    <button disabled={isFedDisable} onClick={handelFood} >Mata djuret</button>
